import React, { useState } from "react";
import styled from "styled-components";
import Form from "react-bootstrap/Form";

const Styles = styled.div`
  .question-container {
  }

  .btns {
    text-align: center;
  }

  .form-group {
    text-align: left;
  }

  .divider {
    border-top: 8px solid lightblue;
    border-radius: 5px;
  }
`;

export default function QuestionForm({ questions }) {
  const [currQuestions, updateQuestions] = useState(questions);

  return (
    <Styles>
      {currQuestions.map((question, index) => (
        <div className="question-container" key={index}>
          <Form>
            <Form.Group>
              <Form.Control type="text" placeholder={question.question} />
            </Form.Group>

            <Form.Group>
              <Form.Control type="text" placeholder={question.answer} />
            </Form.Group>
          </Form>
          <hr className="divider" />
        </div>
      ))}

      <Button variant="primary" type="submit" onClick={(e) => handleSubmit(e)}>
        {submitText}
      </Button>

      <Button variant="danger" type="submit" onClick={(e) => handleReset(e)}>
        Reset
      </Button>
    </Styles>
  );
}
