import React, { useContext, useEffect, useState, useCallback } from "react";
import styled from "styled-components";
import Card from "react-bootstrap/Card";
import Badge from "react-bootstrap/Badge";
import SetForm from "../components/SetForm";
import Collapse from "react-bootstrap/Collapse";
import { DataContext } from "../Store";
import QuestionForm from "../components/QuestionForm";
import Button from "react-bootstrap/Button";

const Styles = styled.div`
  .container {
    width: 500px;
    align-content: center;
  }

  .card {
    padding: 15px;
    text-align: left;
    margin-bottom: 30px;
  }

  .badge {
    margin-left: 5px;
  }

  .btns {
    text-align: center;
  }

  .btn {
    padding: 10px;
  }
`;

export default function Sets() {
  // Global state storing all sets
  const [data, setData] = useContext(DataContext);

  // Tracks if a set is being edited
  const [editedSet, updateSet] = useState({
    setName: "",
    description: "",
    id: null,
    index: null,
    questions: [],
  });

  // Tracks which set questions are being updated
  const [addArr, setShowAdd] = useState([false, false]);

  useEffect(() => {
    console.log(data);
  }, [data]);

  const editSet = (set, editIndex) => {
    updateSet({
      setName: set.setName,
      description: set.description,
      id: set.id,
      index: editIndex,
      questions: set.questions,
    });
  };

  const deleteSet = (index) => {
    let tempData = data;
    tempData.splice(index, 1);

    setData(tempData);
  };

  const practiceSet = (set) => {
    console.log(data);
  };

  const updateEdits = useCallback(
    (newData) => {
      setData(newData);
      console.log("NEXT IS CALLBACK DATA");
      console.log(data);
      console.log("update is being called");

      updateSet({ setName: "", description: "", id: null, index: null, questions: [] });
    },
    [data, setData]
  );

  const toggleAdd = (index) => {
    let tempData = addArr;
    tempData[index] = !tempData[index];
    setShowAdd(tempData);
  };

  return (
    <Styles>
      <SetForm newSet={editedSet} updateCallback={updateEdits} />
      {data.map((set, index) => (
        <div className="container" key={set.id}>
          <Card>
            <Card.Body>
              <Card.Title>
                <h3>
                  {set.setName} <Badge variant="primary">{set.questions.length}</Badge>
                  <Card.Link
                    href="#"
                    style={{ marginLeft: "10px" }}
                    onClick={() => toggleAdd(index)}
                    aria-controls="questions-show"
                    aria-expanded={addArr[index]}
                  >
                    +
                  </Card.Link>
                </h3>
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted">Vocab set</Card.Subtitle>
              <Card.Text>{set.description}</Card.Text>
              <div className="btns">
                <Button variant="outline-primary" className="btn" onClick={() => editSet(set, index)}>
                  Edit
                </Button>

                <Button variant="outline-danger" onClick={() => deleteSet(index)}>
                  Delete
                </Button>

                <Button variant="outline-success" onClick={() => practiceSet(set)}>
                  Practice
                </Button>
                {/* <Card.Link href="#" onClick={() => editSet(set, index)}>
                  Edit
                </Card.Link>
                <Card.Link href="#" style={{ color: "red" }} onClick={() => deleteSet(index)}>
                  Delete
                </Card.Link>
                <Card.Link href="#" style={{ color: "seagreen" }} onClick={() => practiceSet(set)}>
                  Practice
                </Card.Link> */}
              </div>
            </Card.Body>
          </Card>
          <Collapse in={addArr[index]}>
            <div id="questions-show">
              <QuestionForm questions={set.questions} />
            </div>
          </Collapse>
        </div>
      ))}
    </Styles>
  );
}
