import React, { useState, useContext } from "react";
import { DataContext } from "../Store";

import styled from "styled-components";

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const Styles = styled.div`
  .btns {
    text-align: center;
  }

  .form-group {
    text-align: left;
  }

  .divider {
    border-top: 8px solid lightblue;
    border-radius: 5px;
  }

  .question-btns {
    padding-bottom: 20px;
  }

  .btn {
    margin: 15px;
  }
`;

export default function QuestionForm({ questions, currIndex }) {
  // Global state storing all sets
  const [data, setData] = useContext(DataContext);

  const [currQuestions, updateQuestions] = useState(questions);

  const handleSave = (e) => {
    e.preventDefault();

    let tempData = data;
    tempData[currIndex].questions = currQuestions;
    setData(tempData);
  };

  const handleAddQuestion = (e) => {
    e.preventDefault();
    // perform callback
    updateQuestions([...currQuestions, { question: "", answer: "" }]);
  };

  return (
    <Styles>
      {currQuestions.map((question, index) => (
        <div className="question-container" key={index}>
          <Form>
            <Form.Group>
              <Form.Control type="text" defaultValue={question.question} />
            </Form.Group>

            <Form.Group>
              <Form.Control type="text" defaultValue={question.answer} />
            </Form.Group>
          </Form>
          <hr className="divider" />
        </div>
      ))}

      <div className="question-btns">
        <Button variant="primary" type="submit" onClick={(e) => handleSave(e)}>
          Save
        </Button>

        <Button variant="secondary" type="submit" onClick={(e) => handleAddQuestion(e)}>
          Add Question
        </Button>
      </div>
    </Styles>
  );
}
