import React, { useState } from "react";

import styled from "styled-components";
import Jumbotron from "react-bootstrap/Jumbotron";
import Button from "react-bootstrap/Button";

const Styles = styled.div`
  .flashcards-div {
    padding-top: 20px;
    width: 700px;
    align-content: center;
    margin: 0 auto;
  }

  .btn {
    margin: 20px;
    margin-bottom: 0px;
  }
`;

const Practice = (props) => {
  console.log("practice", props.location.inputSet);
  const [showFlashcards, toggleFlashcards] = useState(false);
  const [showWrite, toggleWrite] = useState(false);

  return (
    <Styles>
      <div className="flashcards-div">
        <Jumbotron>
          <h1>Welcome to practice!</h1>
          <h3>Select mode below</h3>
          <p>
            This is a simple hero unit, a simple jumbotron-style component for calling extra attention to featured content or
            information.
          </p>
          <p>
            <Button className="btn" variant="primary">
              Flashcards
            </Button>
            <Button className="btn" variant="primary">
              Write
            </Button>
          </p>
        </Jumbotron>

        <Collapse in={showFlashcards}>
          <div id="flashcards-show">
            <h1>Hello</h1>
          </div>
        </Collapse>

        <Collapse in={showWrite}>
          <div id="write-show">
            <h1>Hello</h1>
          </div>
        </Collapse>
      </div>
    </Styles>
  );
};

export default Practice;
