// import React from "react";
import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import Card from "react-bootstrap/Card";
import Badge from "react-bootstrap/Badge";
import SetForm from "../components/SetForm";

import { DataContext } from "../Store";

const Styles = styled.div`
  .container {
    padding-top: 20px;
    width: 500px;
    align-content: center;
  }

  .card {
    padding: 15px;
    text-align: left;
  }

  .badge {
    margin-left: 5px;
  }

  .btns {
    text-align: center;
  }
`;

export default function Sets() {
  const [data, setData] = useContext(DataContext);
  const [editedSet, updateSet] = useState({ setName: "", description: "", id: null, index: null, questions: [] });

  useEffect(() => {
    console.log(data);
  }, [data]);

  const editSet = (set, editIndex) => {
    updateSet({
      setName: set.setName,
      description: set.description,
      id: set.id,
      index: editIndex,
      questions: set.questions,
    });
  };

  const deleteSet = (index) => {
    let tempData = data;
    tempData.splice(index, 1);

    setData(tempData);
  };

  const practiceSet = (set) => {
    setData(data);
  };

  return (
    <Styles>
      <SetForm newSet={editedSet} />
      {data.map((set, index) => (
        <div className="container" key={index}>
          <Card style={{}}>
            <Card.Body>
              <Card.Title>
                <h3>
                  {set.setName} <Badge variant="primary">{set.questions.length}</Badge>
                  <Card.Link href="#" style={{ marginLeft: "10px" }}>
                    +
                  </Card.Link>
                </h3>
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted">Vocab set</Card.Subtitle>
              <Card.Text>{set.description}</Card.Text>
              <div className="btns">
                <Card.Link href="#" onClick={() => editSet(set, index)}>
                  Edit
                </Card.Link>
                <Card.Link href="#" style={{ color: "red" }} onClick={() => deleteSet(index)}>
                  Delete
                </Card.Link>
                <Card.Link href="#" style={{ color: "seagreen" }} onClick={() => practiceSet(set)}>
                  Practice
                </Card.Link>
              </div>
            </Card.Body>
          </Card>
        </div>
      ))}
    </Styles>
  );
}
