import React, { useState, useContext } from "react";
import styled from "styled-components";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { DataContext } from "../Store";

const Styles = styled.div`
  .container {
    padding-top: 20px;
    width: 500px;
    align-content: center;
  }

  .form-group {
    text-align: left;
  }

  .btn {
    margin: 15px;
    margin-top: 0px;
  }
`;

export default function SetForm() {
  const [formData, setFormData] = useState({ setName: "", description: "" });
  const [data, setData] = useContext(DataContext);

  const handleSetName = (event) => {
    event.persist();
    setFormData((formData) => ({
      ...formData,
      setName: event.target.value,
    }));
    console.log(formData);
  };

  const handleDescription = (event) => {
    event.persist();
    setFormData((formData) => ({
      ...formData,
      description: event.target.value,
    }));
    console.log(formData);
  };

  const handleSubmit = () => {
    console.log("Submitted");
    const insertForm = {
      id: Math.floor(Math.random() * 99999999),
      setName: formData.setName,
      description: formData.description,
      questions: [],
    };
    setData([...data, insertForm]);
    handleReset;
  };

  const handleReset = () => {
    console.log("Submitted");
    setFormData({ setName: "", description: "" });
  };

  return (
    <Styles>
      <div className="container">
        <Form>
          <Form.Group controlId="formBasicEmail" className="form-group">
            <Form.Label>Enter the name of your vocab set</Form.Label>
            <Form.Control placeholder="Name" onChange={handleSetName} value={formData.setName} />
          </Form.Group>

          <Form.Group controlId="formBasicPassword" className="form-group">
            <Form.Label>Enter a short description</Form.Label>
            <Form.Control placeholder="Description" onChange={handleDescription} value={formData.description} />
          </Form.Group>
          <Button variant="primary" type="submit" onClick={handleSubmit}>
            Submit
          </Button>

          <Button variant="danger" type="reset" onClick={handleReset}>
            Reset
          </Button>
        </Form>
      </div>
    </Styles>
  );
}
